import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { addPlace } from '../services/firestoreService';
import { useAuth } from '../contexts/AuthContext';
import { searchPlaces, getStaticMapUrl } from '../services/googleMapsService';

const categories = [
  { id: 'health', name: 'Health' },
  { id: 'food', name: 'Food' },
  { id: 'fitness', name: 'Fitness' },
  { id: 'shopping', name: 'Shopping' },
  { id: 'education', name: 'Education' },
  { id: 'entertainment', name: 'Entertainment' }
];

const AddPlace = () => {
  const [name, setName] = useState('');
  const [category, setCategory] = useState('');
  const [location, setLocation] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [useCurrentLocation, setUseCurrentLocation] = useState(false);
  const [placeQuery, setPlaceQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedPlace, setSelectedPlace] = useState(null);

  const { currentUser } = useAuth();
  const navigate = useNavigate();

  const handleGetCurrentLocation = () => {
    if (navigator.geolocation) {
      setUseCurrentLocation(true);
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setLocation(`${latitude.toFixed(6)}, ${longitude.toFixed(6)}`);
          setUseCurrentLocation(false);
        },
        (error) => {
          console.error('Error getting location:', error);
          setError('Failed to get your current location. Please enter it manually.');
          setUseCurrentLocation(false);
        }
      );
    } else {
      setError('Geolocation is not supported by your browser. Please enter location manually.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name.trim()) {
      return setError('Please enter a place name');
    }

    if (!category) {
      return setError('Please select a category');
    }

    try {
      setError('');
      setLoading(true);

      await addPlace({
        name: name.trim(),
        category,
        location: selectedPlace
          ? `${selectedPlace.location.lat},${selectedPlace.location.lng}`
          : (location.trim() || null),
        address: selectedPlace ? selectedPlace.address : null,
        googlePlaceId: selectedPlace ? selectedPlace.placeId : null,
        addedBy: currentUser.uid,
        addedByEmail: currentUser.email
      });

      navigate('/');
    } catch (error) {
      console.error('Error adding place:', error);
      setError('Failed to add place. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Google Maps Place Search
  const handlePlaceSearch = async () => {
    setError('');
    setSearchResults([]);
    setSelectedPlace(null);
    if (!placeQuery.trim()) return;
    try {
      const results = await searchPlaces(placeQuery.trim());
      setSearchResults(results);
      if (results.length === 0) setError('No places found.');
    } catch (e) {
      setError('Error searching places.');
    }
  };

  const handleSelectPlace = (place) => {
    setSelectedPlace(place);
    setLocation(`${place.location.lat},${place.location.lng}`);
    setSearchResults([]);
    setPlaceQuery(place.name);
  };

  return (
    <div className="max-w-2xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Add a New Place</h1>
        <p className="text-gray-600">
          Help the community by adding a new location to track
        </p>
      </div>

      <div className="bg-white rounded-lg shadow-md p-6">
        {error && (
          <div className="bg-red-50 border border-red-200 rounded-md p-4 mb-6">
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-red-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <p className="text-sm font-medium text-red-800">
                  {error}
                </p>
              </div>
            </div>
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="mb-6">
            <label htmlFor="name" className="block text-gray-700 font-medium mb-2">
              Place Name*
            </label>
            <input
              type="text"
              id="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="input"
              placeholder="e.g., Central Park Gym"
              required
            />
          </div>

          <div className="mb-6">
            <label htmlFor="category" className="block text-gray-700 font-medium mb-2">
              Category*
            </label>
            <select
              id="category"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="input"
              required
            >
              <option value="" disabled>Select a category</option>
              {categories.map((cat) => (
                <option key={cat.id} value={cat.id}>
                  {cat.name}
                </option>
              ))}
            </select>
          </div>

          <div className="mb-6">
            <label htmlFor="location" className="block text-gray-700 font-medium mb-2">
              Location (Search or use coordinates)
            </label>
            <div className="flex mb-2">
              <input
                type="text"
                id="placeQuery"
                value={placeQuery}
                onChange={(e) => setPlaceQuery(e.target.value)}
                className="input rounded-r-none"
                placeholder="Search for a place or address"
              />
              <button
                type="button"
                onClick={handlePlaceSearch}
                className="btn btn-secondary rounded-l-none flex items-center"
                disabled={loading}
              >
                Search
              </button>
            </div>
            {searchResults.length > 0 && (
              <ul className="bg-white border rounded shadow max-h-40 overflow-auto mb-2">
                {searchResults.map((place) => (
                  <li
                    key={place.placeId}
                    className="p-2 hover:bg-blue-100 cursor-pointer"
                    onClick={() => handleSelectPlace(place)}
                  >
                    <div className="font-semibold">{place.name}</div>
                    <div className="text-xs text-gray-600">{place.address}</div>
                  </li>
                ))}
              </ul>
            )}
            <div className="flex">
              <input
                type="text"
                id="location"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                className="input rounded-r-none"
                placeholder="Coordinates (lat,lng)"
              />
              <button
                type="button"
                onClick={handleGetCurrentLocation}
                className="btn btn-secondary rounded-l-none flex items-center"
                disabled={useCurrentLocation}
              >
                {useCurrentLocation ? (
                  <svg className="animate-spin h-5 w-5 mr-2" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                ) : (
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                  </svg>
                )}
                {useCurrentLocation ? 'Getting...' : 'Current'}
              </button>
            </div>
            {selectedPlace && (
              <div className="mt-2">
                <div className="text-xs text-gray-700">Selected: {selectedPlace.name} ({selectedPlace.address})</div>
                <img
                  src={getStaticMapUrl(selectedPlace.location.lat, selectedPlace.location.lng)}
                  alt="Map preview"
                  className="rounded mt-1 border"
                  style={{ width: 300, height: 150 }}
                />
              </div>
            )}
            <p className="text-xs text-gray-500 mt-1">
              Search for a place or enter coordinates, or use the button to get your current location.
            </p>
          </div>

          <div className="flex justify-end">
            <button
              type="button"
              onClick={() => navigate('/')}
              className="btn btn-secondary mr-3"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={loading}
            >
              {loading ? (
                <span className="flex items-center">
                  <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  Adding Place...
                </span>
              ) : 'Add Place'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddPlace;
