import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getPlaces, getRecentReportsByPlace } from '../services/firestoreService';
import { useAuth } from '../contexts/AuthContext';

// Components
import PlaceCard from '../components/PlaceCard';
import ReportModal from '../components/ReportModal';

const categories = [
  { id: 'all', name: 'All Places' },
  { id: 'health', name: 'Health' },
  { id: 'food', name: 'Food' },
  { id: 'fitness', name: 'Fitness' },
  { id: 'shopping', name: 'Shopping' },
  { id: 'education', name: 'Education' },
  { id: 'entertainment', name: 'Entertainment' }
];

const Home = () => {
  const [places, setPlaces] = useState([]);
  const [filteredPlaces, setFilteredPlaces] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [isReportModalOpen, setIsReportModalOpen] = useState(false);

  const { currentUser } = useAuth();

  useEffect(() => {
    const fetchPlaces = async () => {
      try {
        setLoading(true);
        const placesData = await getPlaces();

        // For each place, get the most recent reports
        const placesWithReports = await Promise.all(
          placesData.map(async (place) => {
            const reports = await getRecentReportsByPlace(place.id);

            // Calculate crowd level based on reports
            let crowdLevel = 'Unknown';
            let lastReportTime = null;

            if (reports.length > 0) {
              // Get the average crowd level from recent reports
              const sum = reports.reduce((acc, report) => {
                return acc + report.crowdLevel;
              }, 0);

              const avgLevel = Math.round(sum / reports.length);

              if (avgLevel <= 1) crowdLevel = 'Low';
              else if (avgLevel === 2) crowdLevel = 'Medium';
              else crowdLevel = 'High';

              // Get the most recent report time
              lastReportTime = reports[0].timestamp;
            }

            return {
              ...place,
              crowdLevel,
              lastReportTime,
              reportCount: reports.length
            };
          })
        );

        setPlaces(placesWithReports);
        setFilteredPlaces(placesWithReports);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching places:', error);
        setLoading(false);
      }
    };

    fetchPlaces();
  }, []);

  useEffect(() => {
    // Filter places based on category and search query
    let filtered = places;

    if (selectedCategory !== 'all') {
      filtered = filtered.filter(place => place.category === selectedCategory);
    }

    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(place => 
        place.name.toLowerCase().includes(query) || 
        place.location?.toLowerCase().includes(query)
      );
    }

    setFilteredPlaces(filtered);
  }, [selectedCategory, searchQuery, places]);

  const handleCategoryChange = (categoryId) => {
    setSelectedCategory(categoryId);
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleOpenReportModal = (place) => {
    if (currentUser) {
      setSelectedPlace(place);
      setIsReportModalOpen(true);
    } else {
      // Redirect to login or show login prompt
      alert('Please log in to submit a report');
    }
  };

  const handleCloseReportModal = () => {
    setIsReportModalOpen(false);
    setSelectedPlace(null);
  };

  return (
    <div>
      <section className="mb-8">
        <div className="text-center max-w-3xl mx-auto">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            Know Before You Go
          </h1>
          <p className="text-xl text-gray-600 mb-6">
            Check real-time crowd levels at your favorite places and avoid the rush.
          </p>
          {!currentUser && (
            <div className="flex justify-center space-x-4">
              <Link to="/login" className="btn btn-secondary">
                Login
              </Link>
              <Link to="/register" className="btn btn-primary">
                Sign Up
              </Link>
            </div>
          )}
        </div>
      </section>

      <section className="mb-8">
        <div className="flex flex-col md:flex-row justify-between items-center mb-6">
          <div className="w-full md:w-auto mb-4 md:mb-0">
            <div className="flex overflow-x-auto pb-2 space-x-2">
              {categories.map((category) => (
                <button
                  key={category.id}
                  onClick={() => handleCategoryChange(category.id)}
                  className={`px-4 py-2 rounded-full whitespace-nowrap ${
                    selectedCategory === category.id
                      ? 'bg-primary-600 text-white'
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  {category.name}
                </button>
              ))}
            </div>
          </div>

          <div className="w-full md:w-64">
            <input
              type="text"
              placeholder="Search places..."
              value={searchQuery}
              onChange={handleSearchChange}
              className="input"
            />
          </div>
        </div>

        {loading ? (
          <div className="flex justify-center py-12">
            <div className="w-16 h-16 border-t-4 border-primary-600 border-solid rounded-full animate-spin"></div>
          </div>
        ) : filteredPlaces.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredPlaces.map((place) => (
              <PlaceCard
                key={place.id}
                place={place}
                onReportClick={() => handleOpenReportModal(place)}
              />
            ))}
          </div>
        ) : (
          <div className="text-center py-12">
            <p className="text-gray-600 text-lg">
              No places found. {currentUser && (
                <Link to="/add-place" className="text-primary-600 hover:underline">
                  Add a new place
                </Link>
              )}
            </p>
          </div>
        )}
      </section>

      {currentUser && (
        <div className="fixed bottom-6 right-6">
          <Link
            to="/add-place"
            className="btn btn-primary flex items-center justify-center w-14 h-14 rounded-full shadow-lg"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-6 w-6"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M12 4v16m8-8H4"
              />
            </svg>
          </Link>
        </div>
      )}

      {isReportModalOpen && selectedPlace && (
        <ReportModal
          place={selectedPlace}
          onClose={handleCloseReportModal}
        />
      )}
    </div>
  );
};

export default Home;
