import React, { useEffect, useState } from 'react';
import { getCommentsForPlace, addCommentToPlace } from '../services/firestoreService';
import Toast from './Toast';

const PlaceDetailsModal = ({ place, open, onClose, currentUser }) => {
  const [comments, setComments] = useState([]);
  const [commentText, setCommentText] = useState('');
  const [loading, setLoading] = useState(false);
  const [toast, setToast] = useState({ message: '', type: 'info' });
  const [showToast, setShowToast] = useState(false);

  useEffect(() => {
    if (open && place?.id) {
      fetchComments();
    }
    // eslint-disable-next-line
  }, [open, place?.id]);

  const fetchComments = async () => {
    try {
      const data = await getCommentsForPlace(place.id);
      setComments(data);
    } catch (error) {
      setToast({ message: 'Failed to load comments', type: 'error' });
      setShowToast(true);
    }
  };

  const handleAddComment = async (e) => {
    e.preventDefault();
    if (!commentText.trim()) return;
    setLoading(true);
    try {
      await addCommentToPlace(place.id, {
        text: commentText.trim(),
        userId: currentUser?.uid || 'anonymous',
        userEmail: currentUser?.email || 'Anonymous',
      });
      setCommentText('');
      setToast({ message: 'Comment added!', type: 'success' });
      setShowToast(true);
      fetchComments();
    } catch (error) {
      setToast({ message: 'Failed to add comment', type: 'error' });
      setShowToast(true);
    } finally {
      setLoading(false);
    }
  };

  if (!open || !place) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-40">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-lg p-6 relative">
        <button
          className="absolute top-3 right-3 text-gray-500 hover:text-blue-600 text-xl font-bold"
          onClick={onClose}
          aria-label="Close"
        >
          Ã—
        </button>
        <h2 className="text-2xl font-bold mb-2">{place.name}</h2>
        <p className="text-gray-600 mb-1">{place.address}</p>
        <p className="text-sm text-gray-400 mb-2">Category: {place.category}</p>
        {place.addedByEmail && (
          <p className="text-xs text-gray-400 mb-2">Added by: {place.addedByEmail}</p>
        )}
        <hr className="my-3" />
        <h3 className="font-semibold mb-2">Comments</h3>
        <div className="max-h-40 overflow-y-auto mb-2">
          {comments.length === 0 ? (
            <div className="text-gray-400 text-sm">No comments yet.</div>
          ) : (
            comments.map((c) => (
              <div key={c.id} className="mb-2 p-2 bg-gray-50 rounded">
                <div className="text-xs text-gray-500 mb-1">{c.userEmail || 'Anonymous'}:</div>
                <div className="text-sm">{c.text}</div>
                <div className="text-xs text-gray-300 mt-1">{c.createdAt?.toDate ? c.createdAt.toDate().toLocaleString() : ''}</div>
              </div>
            ))
          )}
        </div>
        {currentUser && (
          <form onSubmit={handleAddComment} className="flex gap-2 mt-2">
            <input
              className="flex-1 border rounded px-2 py-1 text-sm"
              placeholder="Add a comment..."
              value={commentText}
              onChange={e => setCommentText(e.target.value)}
              disabled={loading}
              maxLength={200}
            />
            <button
              type="submit"
              className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700 text-sm"
              disabled={loading || !commentText.trim()}
            >
              Post
            </button>
          </form>
        )}
        {showToast && (
          <Toast message={toast.message} type={toast.type} onClose={() => setShowToast(false)} />
        )}
      </div>
    </div>
  );
};

export default PlaceDetailsModal;
