        </svg>
      );
  }
};

const PlaceCard = ({ place, onReportClick }) => {
  const { currentUser } = useAuth();

  const formatTimestamp = (timestamp) => {
    if (!timestamp) return 'No recent reports';

    try {
      // Convert Firestore timestamp to Date object
      const date = timestamp.toDate ? timestamp.toDate() : new Date(timestamp);
      return formatDistanceToNow(date, { addSuffix: true });
    } catch (error) {
      console.error('Error formatting timestamp:', error);
      return 'Unknown time';
    }
  };

  return (
    <div className="card hover:shadow-lg">
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center">
          <div className="p-2 rounded-md bg-primary-100 text-primary-600 mr-3">
            {getCategoryIcon(place.category)}
          </div>
          <h3 className="font-semibold text-lg">{place.name}</h3>
        </div>
        <div className={`px-3 py-1 rounded-full text-sm font-medium flex items-center ${getCrowdLevelColor(place.crowdLevel)}`}>
          <span className="mr-1">{getCrowdLevelIcon(place.crowdLevel)}</span>
          {place.crowdLevel}
        </div>
      </div>

      <div className="mb-4">
        {place.location && (
          <div className="flex flex-col mb-2">
            <div className="flex items-center text-gray-500 text-sm">
              <svg className="h-4 w-4 mr-1" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                <path strokeLinecap="round" strokeLinejoin="round" d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
              </svg>
              {place.address ? (
                <span>{place.address}</span>
              ) : (
                <span>{place.location}</span>
              )}
            </div>
            {/* Show map if coordinates are available */}
            {place.location && place.location.includes(',') && (
              <img
                src={getOsmStaticMapUrl(
                  parseFloat(place.location.split(',')[0]),
                  parseFloat(place.location.split(',')[1])
                )}
                alt="Map preview from OpenStreetMap"
                className="rounded mt-1 border"
                style={{ width: 250, height: 100 }}
              />
            )}
            {place.location && place.location.includes(',') && (
              <div className="text-xs text-gray-400 mt-1">Map by <a href="https://www.openstreetmap.org/" className="underline" target="_blank" rel="noopener noreferrer">OpenStreetMap</a></div>
            )}
          </div>
        )}

        <div className="flex items-center text-sm text-gray-500">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-12a1 1 0 10-2 0v4a1 1 0 00.293.707l2.828 2.829a1 1 0 101.415-1.415L11 9.586V6z" clipRule="evenodd" />
          </svg>
          {formatTimestamp(place.lastReportTime)}
        </div>

        <div className="text-sm text-gray-500 mt-1">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 inline mr-1" viewBox="0 0 20 20" fill="currentColor">
            <path d="M9 6a3 3 0 11-6 0 3 3 0 016 0zM17 6a3 3 0 11-6 0 3 3 0 016 0zM12.93 17c.046-.327.07-.66.07-1a6.97 6.97 0 00-1.5-4.33A5 5 0 0119 16v1h-6.07zM6 11a5 5 0 015 5v1H1v-1a5 5 0 015-5z" />
          </svg>
          {place.reportCount} {place.reportCount === 1 ? 'report' : 'reports'}
        </div>
      </div>

      <button
        onClick={() => onReportClick()}
        className={`w-full py-2 rounded-md font-medium transition-colors duration-200 ${
          currentUser 
            ? 'bg-primary-600 text-white hover:bg-primary-700' 
            : 'bg-gray-200 text-gray-500 cursor-not-allowed'
        }`}
        disabled={!currentUser}
      >
        {currentUser ? 'Submit Report' : 'Login to Report'}
      </button>
    </div>
  );
};

export default PlaceCard;
